tasks.command:
cat >> style.css <<EOF

/* Social Links */
.social-links {
  display: flex;
  gap: 12px;
  margin-top: 8px;
}

.social-icon {
  color: #a0a0a0;
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  text-decoration: none;
}

.social-icon:hover {
  color: #ffffff;
  transform: translateY(-1px);
}

.social-icon svg {
  width: 18px;
  height: 18px;
  fill: currentColor;
}
EOF
